ys\",\"value\":\"30\",\"key\":\"target\",\"isMandatory\":true},{\"isValueNew\":true,\"encrypt\":false,\"value\":\"/aws_ec2/qualysinfo/_search\",\"key\":\"esQualysUrl\"},{\"isValueNew\":true,\"defaultVal\":\"7\",\"encrypt\":false,\"isEdit\":true,\"displayName\":\"Discovered days range\",\"description\":\"Discovered days in number\",\"value\":\"7\",\"key\":\"discoveredDaysRange\",\"isMandatory\":true},{\"isValueNew\":true,\"encrypt\":false,\"value\":\"check-for-resource-scanned-by-qualys\",\"key\":\"policyKey\"},{\"encrypt\":false,\"value\":\"critical\",\"key\":\"severity\"},{\"encrypt\":false,\"value\":\"security\",\"key\":\"policyCategory\"}],\"autofix\":false,\"alexaKeyword\":\"Ec2InstanceScannedByQualysAPI\"}','0 0 ? * MON *','','','ManagePolicy','arn:aws:events:us-east-1:***REMOVED***:rule/aws_ec2_qualys_scanned_rule','critical','security','false','false',NULL,24,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,24,'ASGC','2019-09-18','2019-09-18','ENABLED');
