rn:aws:events:us-east-1:***REMOVED***:rule/aws_remove_root_user_account_access_key','medium','security','false','false',NULL,24,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,24,'','2022-05-12','2022-05-12','ENABLED');
