",\"isValueNew\":true,\"encrypt\":false},{\"key\":\"name\",\"value\":\"VirtualMachines\",\"isValueNew\":true,\"encrypt\":false},{\"key\":\"policyName\",\"value\":\"VPC Firewall with RPC  port 135  should not be publicly accessible\",\"isValueNew\":true,\"encrypt\":false},{\"encrypt\":false,\"value\":\"critical\",\"key\":\"severity\"},{\"encrypt\":false,\"value\":\"security\",\"key\":\"policyCategory\"},{\"encrypt\":false,\"value\":\"\",\"key\":\"policyOwner\"}],\"environmentVariables\":[],\"policyId\":\"VPC_firewall_RPC_port_should_not_be_publicly_accessible\",\"autofix\":false,\"alexaKeyword\":\"VPC_firewall_RPC_port_should_not_be_publicly_accessible\",\"policyRestUrl\":\"\",\"targetType\":\"vpcfirewall\",\"pac_ds\":\"gcp\",\"assetGroup\":\"gcp\",\"policyUUID\":\"VPC_firewall_RPC_port_should_not_be_publicly_accessible\",\"policyType\":\"ManagePolicy\"}','0 0/6 * * ? *',NULL,NULL,'ManagePolicy','arn:aws:events:us-east-1:***REMOVED***:rule/VPC_firewall_RPC_port_should_not_be_publicly_accessible','critical','security','false','false',NULL,24,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,24,'ASGC','2022-06-09','2022-06-09','ENABLED');
