ry":true},{"isValueNew":true,"encrypt":false,"value":"check-for-vms-scanned-by-tenable","key":"policyKey"},{"encrypt":false,"value":"high","key":"severity"},{"encrypt":false,"value":"security","key":"policyCategory"}],"autofix":false,"alexaKeyword":"AWSVMScannedByTenable"}','0 0 ? * MON *','','','ManagePolicy','arn:aws:events:us-east-1:***REMOVED***:rule/aws_vm_tenable_scanned_rule','ENABLED','ASGC',now(),now(),'high','security',false,false,null,24,1,null,null,null,null,null,null,null,null,24);
