INSERT  IGNORE INTO cf_PolicyTable (policyId, policyUUID, policyName, policyDisplayName, policyDesc, resolution, resolutionUrl, targetType, assetGroup, alexaKeyword, policyParams, policyFrequency, policyExecutable, policyRestUrl, policyType, policyArn, status, userId, createdDate, modifiedDate, severity, category) VALUES ('TLS_Version_MySQL_Flexible_Server','TLS_Version_MySQL_Flexible_Server','latestTLSVersion','Ensure that MySQL Flexible Database Server has the Latest TLS Version','Ensure that latest version of TLS encryption protocol to secure the applications traffic over the Internet and comply with the industry standards.','For web app : \n 1.Go to Azure portal \n 2.Navigate to App Services blade at https://portal.azure.com/#blade/HubsExtension/BrowseResourceBlade/resourceType/Microsoft.Web%2Fsites. \n 3.Click on the name of the App Service web application that you want to examine. \n 4.In the navigation panel, under Settings, select TLS/SSL settings to access the Transport Layer Security (TLS)/Secure Sockets Layer (SSL) configuration settings available for the selected app. \n 5.On the TLS/SSL settings panel, under Protocol Settings, check the Minimum TLS Version configuration to determine the TLS version used by the application. If Minimum TLS Version setting value is not set to 1.2, the selected Microsoft Azure App Service web application is not configured to use the latest version of TLS protocol (i.e. TLS 1.2) to encrypt data in transit.\nFor mysql flexible server: \n 1. Go to azure portal \n 2. Search for MySQL flexible server \n 3. For each database , click on on Server parameters under Settings \n 4. Type tls_version in search box \n 5. Select value as TLSV1.2 \nFor storage account: \n 1.Go to Azure portal \n 2.Navigate to App Services blade at https://portal.azure.com/#blade/HubsExtension/BrowseResourceBlade/resourceType/Microsoft.Storage%2FStorageAccounts. \n 3.Click on the name of the Azure Storage accoun  that you want to examine. \n 4.In the navigation panel, under Settings, choose Configuration to access the selected storage account configuration settings. \n 5On the Configuration page, under Minimum TLS Version, select Version 1.2 to enable the latest version of the TLS protocol (TLS version 1.2) for the selected Microsoft Azure Storage account. Choose Save to apply the changes.','https://github.com/PaladinCloud/CE/wiki/Azure-Policy#enable-latest-tls-version','mysqlflexible','azure','MysqlFlexibleTLS','{\"params\":[{\"encrypt\":false,\"value\":\"check-for-tls-version-mysql-flexible-server\",\"key\":\"policyKey\"},{\"encrypt\":false,\"value\":\",\",\"key\":\"splitterChar\"},{\"encrypt\":false,\"value\":\"Application,Environment,Stack,Role\",\"key\":\"mandatoryTags\",\"isEdit\":true,\"isMandatory\":true,\"description\":\"Assets should have these mandatory tags\",\"defaultVal\":\"Application,Environment,Stack,Role\",\"displayName\":\"Mandatory tags\"},{\"encrypt\":false,\"value\":\"TLSV1.2\",\"key\":\"supportedTlsVersions\",\"isEdit\":true,\"isMandatory\":true,\"description\":\"Minimum TLS version needed\",\"defaultVal\":\"TLSV1.2\",\"displayName\":\"Supported TLS versions\"},{\"encrypt\":false,\"value\":\"medium\",\"key\":\"severity\"},{\"encrypt\":false,\"value\":\"security\",\"key\":\"policyCategory\"},{\"encrypt\":false,\"value\":\"\",\"key\":\"policyOwner\"}],\"environmentVariables\":[],\"policyId\":\"TLS_Version_MySQL_Flexible_Server\",\"autofix\":false,\"alexaKeyword\":\"MysqlFlexibleTLS\",\"policyRestUrl\":\"\",\"targetType\":\"mysqlflexible\",\"pac_ds\":\"azure\",\"assetGroup\":\"azure\",\"policyUUID\":\"TLS_Version_MySQL_Flexible_Server\",\"policyType\":\"ManagePolicy\"}','0 0/6 * * ? *',NULL,NULL,'ManagePolicy','arn:aws:events:us-east-1:***REMOVED***:rule/TLS_Version_MySQL_Flexible_Server','ENABLED','ASGC','2022-09-06','2022-09-06','medium','security');
