USE `pacmandata`;

CREATE TABLE IF NOT EXISTS `cf_PolicyTable` (
  `policyId` varchar(200) COLLATE utf8_bin NOT NULL,
  `policyUUID` varchar(200) COLLATE utf8_bin NOT NULL,
  `policyName` varchar(200) COLLATE utf8_bin DEFAULT NULL,
  `policyDisplayName` varchar(100) COLLATE utf8_bin DEFAULT NULL,
  `policyDesc` text COLLATE utf8_bin,
  `resolution` longtext COLLATE utf8_bin,
  `resolutionUrl` varchar(500) COLLATE utf8_bin DEFAULT NULL,
  `targetType` varchar(75) COLLATE utf8_bin DEFAULT NULL,
  `assetGroup` varchar(100) COLLATE utf8_bin DEFAULT NULL,
  `alexaKeyword` varchar(100) COLLATE utf8_bin DEFAULT NULL,
  `policyParams` longtext COLLATE utf8_bin,
  `policyFrequency` varchar(75) COLLATE utf8_bin DEFAULT NULL,
  `policyExecutable` varchar(75) COLLATE utf8_bin DEFAULT NULL,
  `policyRestUrl` varchar(500) COLLATE utf8_bin DEFAULT NULL,
  `policyType` varchar(75) COLLATE utf8_bin DEFAULT NULL,
  `policyArn` varchar(150) COLLATE utf8_bin DEFAULT NULL,
  `status` varchar(75) COLLATE utf8_bin DEFAULT NULL,
  `userId` varchar(75) COLLATE utf8_bin DEFAULT NULL,
  `createdDate` date DEFAULT NULL,
  `modifiedDate` date DEFAULT NULL,
  `severity` varchar(20) COLLATE utf8_bin DEFAULT NULL,
  `category` varchar(50) COLLATE utf8_bin DEFAULT NULL,
  PRIMARY KEY (`policyId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;


CREATE TABLE IF NOT EXISTS cf_PolicyPack (
  policyPackId varchar(200) COLLATE utf8_bin NOT NULL,
  policyPackName varchar(200) COLLATE utf8_bin NOT NULL,
  policyPackDescription longtext,
  PRIMARY KEY (policyPackId)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS cf_PolicyPackInfo (
	policyPackInfoId varchar(200)  NOT NULL,
    childPolicyPackId varchar(200) COLLATE utf8_bin DEFAULT NULL,
    parentPolicyPackId varchar(200) COLLATE utf8_bin NOT NULL,
    PRIMARY KEY (policyPackInfoId),
    CONSTRAINT child_policy_pack_id_fkey FOREIGN KEY (childPolicyPackId) REFERENCES cf_PolicyPack (policyPackId) ON DELETE CASCADE ON UPDATE CASCADE,
    CONSTRAINT parent_policy_pack_id_fkey FOREIGN KEY (parentPolicyPackId) REFERENCES cf_PolicyPack (policyPackId) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS cf_PolicyPackRuleInfo (
	policyPackpolicyId varchar(200) NOT NULL,
	policyId varchar(200) COLLATE utf8_bin NOT NULL,
    policyPackId varchar(200) COLLATE utf8_bin NOT NULL,
    primary key (policyPackpolicyId),
    constraint policy_pack_id_fkey FOREIGN KEY (policyPackId) REFERENCES cf_PolicyPack (policyPackId) ON DELETE CASCADE ON UPDATE CASCADE,
    constraint policy_id_fkey FOREIGN KEY (policyId) REFERENCES cf_Policy (policyId) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;






